package com.home.commonClient.net.response.scene.scene;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.net.response.scene.base.SceneSResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送副本类场景状态信息(generated by shine) */
public class SendBattleStateResponse extends SceneSResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.SendBattleState;
	
	/** 副本当前状态 */
	public int state;
	
	/** 剩余tick时间(-1就是没在tick) */
	public int timeTick;
	
	public SendBattleStateResponse()
	{
		_dataID=GameResponseType.SendBattleState;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendBattleStateResponse";
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		scene.getBattleScenePlayLogic().onRefreshBattleState(state,timeTick);
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.state=stream.readInt();
		
		this.timeTick=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.state=stream.readInt();
		
		this.timeTick=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("state");
		writer.sb.append(':');
		writer.sb.append(this.state);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("timeTick");
		writer.sb.append(':');
		writer.sb.append(this.timeTick);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.state=0;
		this.timeTick=0;
	}
	
}
