package com.home.commonClient.net.response.scene.unit.building;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.net.response.scene.base.UnitSResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 建筑升级成功的广播协议 注意:包含父类参数 instanceID(generated by shine) */
public class BuildingLevelUpingCompleteResponse extends UnitSResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.BuildingLevelUpingComplete;
	
	/** 下一等级 */
	public int level;
	
	public BuildingLevelUpingCompleteResponse()
	{
		_dataID=GameResponseType.BuildingLevelUpingComplete;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "BuildingLevelUpingCompleteResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.level=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.level=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("level");
		writer.sb.append(':');
		writer.sb.append(this.level);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.level=0;
	}
	
}
