package com.home.commonClient.net.response.system;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 收到客户端离线事务消息(generated by shine) */
public class ReceiveClientOfflineWorkResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.ReceiveClientOfflineWork;
	
	/** 收到的序号 */
	public int receiveIndex;
	
	/** 是否执行成功 */
	public boolean success;
	
	public ReceiveClientOfflineWorkResponse()
	{
		_dataID=GameResponseType.ReceiveClientOfflineWork;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ReceiveClientOfflineWorkResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.receiveIndex=stream.readInt();
		
		this.success=stream.readBoolean();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.receiveIndex=stream.readInt();
		
		this.success=stream.readBoolean();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("receiveIndex");
		writer.sb.append(':');
		writer.sb.append(this.receiveIndex);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("success");
		writer.sb.append(':');
		writer.sb.append(this.success);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.receiveIndex=0;
		this.success=false;
	}
	
}
