package com.home.commonClient.net.sceneBaseRequest.unit;
import com.home.commonBase.data.scene.base.DriveData;
import com.home.commonBase.data.scene.base.PosDirData;
import com.home.commonClient.constlist.generate.GameRequestType;
import com.home.commonClient.constlist.generate.SceneBaseRequestType;
import com.home.commonClient.net.sceneBaseRequest.base.CUnitRRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 单位驾驶消息(generated by shine) */
public class CUnitDriveRequest extends CUnitRRequest
{
	/** 数据类型ID */
	public static final int dataID=SceneBaseRequestType.CUnitDrive;
	
	/** 驾驶数据 */
	public DriveData data;
	
	/** 当前位置 */
	public PosDirData nowPos;
	
	public CUnitDriveRequest()
	{
		_dataID=SceneBaseRequestType.CUnitDrive;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		PosDirData nowPosTemp=nowPos;
		if(nowPosTemp!=null)
		{
			this.nowPos=new PosDirData();
			this.nowPos.copy(nowPosTemp);
		}
		else
		{
			this.nowPos=null;
		}
		
		DriveData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(DriveData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CUnitDriveRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.nowPos!=null)
		{
			stream.writeBoolean(true);
			this.nowPos.writeBytesFull(stream);
		}
		else
		{
			stream.writeBoolean(false);
		}
		
		if(this.data!=null)
		{
			stream.writeDataFullNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.nowPos!=null)
		{
			stream.writeBoolean(true);
			this.nowPos.writeBytesSimple(stream);
		}
		else
		{
			stream.writeBoolean(false);
		}
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("nowPos");
		writer.sb.append(':');
		if(this.nowPos!=null)
		{
			this.nowPos.writeDataString(writer);
		}
		else
		{
			writer.sb.append("PosDirData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("DriveData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		if(this.nowPos!=null)
		{
			this.nowPos.release(pool);
			this.nowPos=null;
		}
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static CUnitDriveRequest create(int instanceID,PosDirData nowPos,DriveData data)
	{
		CUnitDriveRequest re=(CUnitDriveRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.nowPos=nowPos;
		re.data=data;
		return re;
	}
	
}
