package com.home.commonClient.net.sceneBaseResponse.syncScene;
import com.home.commonBase.data.scene.fight.FrameSyncData;
import com.home.commonClient.constlist.generate.GameResponseType;
import com.home.commonClient.constlist.generate.SceneBaseResponseType;
import com.home.commonClient.net.sceneBaseResponse.base.SceneSResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 帧同步每帧消息(generated by shine) */
public class FrameSyncFrameResponse extends SceneSResponse
{
	/** 数据类型ID */
	public static final int dataID=SceneBaseResponseType.FrameSyncFrame;
	
	/** 帧数据 */
	public FrameSyncData frame;
	
	public FrameSyncFrameResponse()
	{
		_dataID=SceneBaseResponseType.FrameSyncFrame;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FrameSyncFrameResponse";
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData frameT=stream.readDataFullNotNull();
		if(frameT!=null)
		{
			if(frameT instanceof FrameSyncData)
			{
				this.frame=(FrameSyncData)frameT;
			}
			else
			{
				this.frame=new FrameSyncData();
				if(!(frameT.getClass().isAssignableFrom(FrameSyncData.class)))
				{
					stream.throwTypeReadError(FrameSyncData.class,frameT.getClass());
				}
				this.frame.shadowCopy(frameT);
			}
		}
		else
		{
			this.frame=null;
		}
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.frame=(FrameSyncData)stream.readDataSimpleNotNull();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("frame");
		writer.sb.append(':');
		if(this.frame!=null)
		{
			this.frame.writeDataString(writer);
		}
		else
		{
			writer.sb.append("FrameSyncData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.frame=null;
	}
	
}
