package com.home.commonClient.net.sceneRequest.base;
import com.home.commonClient.constlist.generate.SceneRequestType;
import com.home.commonClient.net.base.SceneRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;

/** 场景服登录消息(generated by shine) */
public class SceneLoginRequest extends SceneRequest
{
	/** 数据类型ID */
	public static final int dataID=SceneRequestType.SceneLogin;
	
	public SceneLoginRequest()
	{
		_dataID=SceneRequestType.SceneLogin;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SceneLoginRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.endWriteObj();
	}
	
	/** 创建实例 */
	public static SceneLoginRequest create()
	{
		SceneLoginRequest re=(SceneLoginRequest)BytesControl.createRequest(dataID);
		return re;
	}
	
}
