package com.home.commonBase.config.game;
import com.home.commonBase.config.base.BaseConfig;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.support.collection.IntObjectMap;
import com.home.shine.tool.SectionTool;

/** 区服信息(generated by shine) */
public class AreaInfoConfig extends BaseConfig
{
	/** 存储集合 */
	private static IntObjectMap<AreaInfoConfig> _dic;
	
	/** id */
	public int id;
	
	/** 名字 */
	public String name="";
	
	/** 获取 */
	public static AreaInfoConfig get(int id)
	{
		return _dic.get(id);
	}
	
	/** 设置字典 */
	public static void setDic(IntObjectMap<AreaInfoConfig> dic)
	{
		_dic=dic;
	}
	
	/** 获取全部 */
	public static IntObjectMap<AreaInfoConfig> getDic()
	{
		return _dic;
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.name=stream.readUTF();
		
	}
	
	/** 读完所有表后处理 */
	public static void afterReadConfigAll()
	{
	
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeUTF(this.name);
		
	}
	
	public static String getAreaName(int id)
	{
		AreaInfoConfig config=get(id);
		
		if(config==null)
			return "";
		
		return config.name;
	}
	
}
