package com.home.commonBase.config.game;
import com.home.commonBase.config.base.BaseConfig;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.support.collection.LongObjectMap;
import com.home.shine.support.collection.SMap;

/** 单位显示部件表(generated by shine) */
public class AvatarPartConfig extends BaseConfig
{
	/** 存储集合 */
	private static LongObjectMap<AvatarPartConfig> _dic;
	
	/** 部件类型 */
	public int type;
	
	/** 部件ID */
	public int id;
	
	/** 优先级 */
	public int proirity;
	
	/** 获取 */
	public static AvatarPartConfig get(int type,int id)
	{
		return _dic.get((long)type << 32 | (long)id);
	}
	
	/** 设置字典 */
	public static void setDic(LongObjectMap<AvatarPartConfig> dic)
	{
		_dic=dic;
	}
	
	/** 获取全部 */
	public static LongObjectMap<AvatarPartConfig> getDic()
	{
		return _dic;
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.type=stream.readInt();
		
		this.id=stream.readInt();
		
		this.proirity=stream.readInt();
		
	}
	
	/** 读完所有表后处理 */
	public static void afterReadConfigAll()
	{
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.type);
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.proirity);
		
	}
	
}
