package com.home.commonBase.config.game;
import com.home.commonBase.config.base.BaseConfig;
import com.home.commonBase.config.game.LanguageConfig;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.support.collection.IntObjectMap;

/** 信息日志表(generated by shine) */
public class InfoLogConfig extends BaseConfig
{
	/** 存储集合 */
	private static IntObjectMap<InfoLogConfig> _dic;
	
	/** 信息id */
	public int id;
	
	/** 是否需要存库 */
	public boolean needSave;
	
	/** 文字 */
	public String text="";
	
	/** 文字原值 */
	private String _text;
	
	/** 获取 */
	public static InfoLogConfig get(int id)
	{
		return _dic.get(id);
	}
	
	/** 设置字典 */
	public static void setDic(IntObjectMap<InfoLogConfig> dic)
	{
		_dic=dic;
	}
	
	/** 获取全部 */
	public static IntObjectMap<InfoLogConfig> getDic()
	{
		return _dic;
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.text=stream.readUTF();
		
		this.needSave=stream.readBoolean();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeUTF(this.text);
		
		stream.writeBoolean(this.needSave);
		
	}
	
	/** 读完所有表后处理 */
	public static void afterReadConfigAll()
	{
		
	}
	
	/** 生成刷新配置 */
	@Override
	protected void generateRefresh()
	{
		if(_text==null)
			_text=text;
		text=LanguageConfig.getText(_text);
		
	}
	
}
