package com.home.commonBase.config.game;
import com.home.commonBase.config.base.BaseConfig;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.support.collection.LongObjectMap;

/** 玩家群等级表(generated by shine) */
public class RoleGroupLevelConfig extends BaseConfig
{
	/** 存储集合 */
	private static LongObjectMap<RoleGroupLevelConfig> _dic;
	
	/** id */
	public int id;
	
	/** 等级 */
	public int level;
	
	/** 经验上限 */
	public long expMax;
	
	/** 最多人数 */
	public int maxNum;
	
	/** 获取 */
	public static RoleGroupLevelConfig get(int id,int level)
	{
		return _dic.get((long)id << 32 | (long)level);
	}
	
	/** 设置字典 */
	public static void setDic(LongObjectMap<RoleGroupLevelConfig> dic)
	{
		_dic=dic;
	}
	
	/** 获取全部 */
	public static LongObjectMap<RoleGroupLevelConfig> getDic()
	{
		return _dic;
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.level=stream.readInt();
		
		this.maxNum=stream.readInt();
		
		this.expMax=stream.readLong();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.level);
		
		stream.writeInt(this.maxNum);
		
		stream.writeLong(this.expMax);
		
	}
	
	/** 读完所有表后处理 */
	public static void afterReadConfigAll()
	{
		RoleGroupConfig config;
		
		RoleGroupLevelConfig[] values;
		RoleGroupLevelConfig v;
		
		for(int i=(values=_dic.getValues()).length-1;i>=0;--i)
		{
			if((v=values[i])!=null)
			{
				config=RoleGroupConfig.get(v.id);
				
				if(config.levelMax<v.level)
					config.levelMax=v.level;
			}
		}
	}
	
}
