package com.home.commonBase.config.game.enumT;
import com.home.commonBase.config.base.BaseConfig;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;

/** 拍卖行查询条件类型(generated by shine) */
public class AuctionQueryConditionTypeConfig extends BaseConfig
{
	/** 存储集合 */
	private static AuctionQueryConditionTypeConfig[] _dic;
	
	/** 是否降序 */
	public boolean isDesc;
	
	/** 比较方式 */
	public int compareType;
	
	/** id */
	public int id;
	
	/** 获取 */
	public static AuctionQueryConditionTypeConfig get(int id)
	{
		return id>=0 && id<_dic.length ? _dic[id] : null;
	}
	
	/** 设置字典 */
	public static void setDic(AuctionQueryConditionTypeConfig[] dic)
	{
		_dic=dic;
	}
	
	/** 获取全部 */
	public static AuctionQueryConditionTypeConfig[] getDic()
	{
		return _dic;
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.compareType=stream.readInt();
		
		this.isDesc=stream.readBoolean();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.compareType);
		
		stream.writeBoolean(this.isDesc);
		
	}
	
	/** 读完所有表后处理 */
	public static void afterReadConfigAll()
	{
		
	}
	
}
