package com.home.commonBase.config.game.enumT;
import com.home.commonBase.config.base.BaseConfig;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.support.collection.SMap;

/** 角色显示数据部件类型(generated by shine) */
public class RoleShowDataPartTypeConfig extends BaseConfig
{
	/** 存储集合 */
	private static RoleShowDataPartTypeConfig[] _dic;
	
	/** id */
	public int id;
	
	/** 是否需要实时推送 */
	public boolean needSendAbs;
	
	/** 是否为社交字段 */
	public boolean isSocialPart;
	
	/** 获取 */
	public static RoleShowDataPartTypeConfig get(int id)
	{
		return id>=0 && id<_dic.length ? _dic[id] : null;
	}
	
	/** 设置字典 */
	public static void setDic(RoleShowDataPartTypeConfig[] dic)
	{
		_dic=dic;
	}
	
	/** 获取全部 */
	public static RoleShowDataPartTypeConfig[] getDic()
	{
		return _dic;
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.isSocialPart=stream.readBoolean();
		
		this.needSendAbs=stream.readBoolean();
		
	}
	
	/** 读完所有表后处理 */
	public static void afterReadConfigAll()
	{
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeBoolean(this.isSocialPart);
		
		stream.writeBoolean(this.needSendAbs);
		
	}
	
}
