package com.home.commonBase.config.game.enumT;
import com.home.commonBase.config.base.BaseConfig;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;

/** 场景实例类型(generated by shine) */
public class SceneInstanceTypeConfig extends BaseConfig
{
	/** 存储集合 */
	private static SceneInstanceTypeConfig[] _dic;
	
	/** id */
	public int id;
	
	/** 是否在玩家全部离开场景后立即移除场景 */
	public boolean removeAfterPlayerAllLeave;
	
	/** 获取 */
	public static SceneInstanceTypeConfig get(int id)
	{
		return id>=0 && id<_dic.length ? _dic[id] : null;
	}
	
	/** 设置字典 */
	public static void setDic(SceneInstanceTypeConfig[] dic)
	{
		_dic=dic;
	}
	
	/** 获取全部 */
	public static SceneInstanceTypeConfig[] getDic()
	{
		return _dic;
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.removeAfterPlayerAllLeave=stream.readBoolean();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeBoolean(this.removeAfterPlayerAllLeave);
		
	}
	
	/** 读完所有表后处理 */
	public static void afterReadConfigAll()
	{
		
	}
	
}
