package com.home.commonBase.constlist.generate;

/** 随机物品方式(generated by shine) */
public class RandomItemType
{
	/** 单个物品(id,[num]) */
	public static final int Single=1;
	
	/** 等权物品组(id,[num];id,[num]) */
	public static final int AverageItem=2;
	
	/** 权重随机物品组(weight,id,[num];weight,id,[num],id,[num]) */
	public static final int WeightItem=3;
	
	/** 权重随机创建物品组(weight,createID,[num];weight,createID,[num]) */
	public static final int WeightCreateItem=4;
	
	/** 权重随机随机物品组(嵌套)(weight,randomItemID;weight,randomItemID) */
	public static final int WeightRandomItem=5;
	
	/** 长度 */
	public static int size=6;
	
}
