package com.home.commonBase.constlist.generate;

/** 状态类型表(generated by shine) */
public class StatusType
{
	/** 死亡 */
	public static final int IsDead=1;
	
	/** 处于战斗状态 */
	public static final int OnFight=2;
	
	/** 不可被选择(蝗虫群) */
	public static final int CantBeTarget=3;
	
	/** 不可移动 */
	public static final int CantMove=4;
	
	/** 不可转身 */
	public static final int CantTurn=5;
	
	/** 不可攻击 */
	public static final int CantAttack=6;
	
	/** 不可施法 */
	public static final int CantCast=7;
	
	/** 眩晕(等价于以上4个) */
	public static final int Vertigo=8;
	
	/** 控制免疫 */
	public static final int ControlImmun=9;
	
	/** 伤害免疫 */
	public static final int DamageImmun=10;
	
	/** 敌对不可选(无敌) */
	public static final int CantBeEnemyTarget=11;
	
	/** 完整无敌(等价于以上3个加不可被伤害击杀) */
	public static final int Invincible=12;
	
	/** 绝对命中(克制绝对闪避) */
	public static final int AbsHit=13;
	
	/** 绝对闪避 */
	public static final int AbsDodge=14;
	
	/** 绝对暴击 */
	public static final int AbsCrit=15;
	
	/** 绝对韧性(克制绝对暴击) */
	public static final int AbsTenacity=16;
	
	/** 隐身 */
	public static final int Hide=17;
	
	/** 安全区(mmo) */
	public static final int SafeRegion=18;
	
	/** 吸收子弹(盾墙) */
	public static final int AbsorbBullet=19;
	
	/** 不可被攻击(attack跳过) */
	public static final int CantBeAttackTarget=20;
	
	/** 不可被伤害击杀(暗牧-薄葬,蛮王大) */
	public static final int CantBeKillByDamage=21;
	
	/** 不可被治疗 */
	public static final int CantBeHeal=22;
	
	/** 技能免疫(弱于敌对不可选) */
	public static final int SkillImmun=23;
	
	/** 长度 */
	public static int size=24;
	
}
