package com.home.commonBase.data.item;
import com.home.commonBase.constlist.generate.BaseDataType;
import com.home.commonBase.data.mail.MailData;
import com.home.commonBase.data.role.RoleShowData;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 玩家邮件(generated by shine) */
public class PlayerMailData extends MailData
{
	/** 数据类型ID */
	public static final int dataID=BaseDataType.PlayerMail;
	
	/** 角色数据 */
	public RoleShowData roleData;
	
	public PlayerMailData()
	{
		_dataID=BaseDataType.PlayerMail;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "PlayerMailData";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData roleDataT=stream.readDataFullNotNull();
		if(roleDataT!=null)
		{
			if(roleDataT instanceof RoleShowData)
			{
				this.roleData=(RoleShowData)roleDataT;
			}
			else
			{
				this.roleData=new RoleShowData();
				if(!(roleDataT.getClass().isAssignableFrom(RoleShowData.class)))
				{
					stream.throwTypeReadError(RoleShowData.class,roleDataT.getClass());
				}
				this.roleData.shadowCopy(roleDataT);
			}
		}
		else
		{
			this.roleData=null;
		}
		
		stream.endReadObj();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.roleData!=null)
		{
			stream.writeDataFullNotNull(this.roleData);
		}
		else
		{
			nullObjError("roleData");
		}
		
		stream.endWriteObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.roleData=(RoleShowData)stream.readDataSimpleNotNull();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.roleData!=null)
		{
			stream.writeDataSimpleNotNull(this.roleData);
		}
		else
		{
			nullObjError("roleData");
		}
		
	}
	
	/** 复制(潜拷贝) */
	@Override
	protected void toShadowCopy(BaseData data)
	{
		super.toShadowCopy(data);
		
		if(!(data instanceof PlayerMailData))
			return;
		
		PlayerMailData mData=(PlayerMailData)data;
		
		this.roleData=mData.roleData;
	}
	
	/** 复制(深拷贝) */
	@Override
	protected void toCopy(BaseData data)
	{
		super.toCopy(data);
		
		if(!(data instanceof PlayerMailData))
			return;
		
		PlayerMailData mData=(PlayerMailData)data;
		
		if(mData.roleData!=null)
		{
			this.roleData=(RoleShowData)mData.roleData.clone();
		}
		else
		{
			this.roleData=null;
			nullObjError("roleData");
		}
		
	}
	
	/** 是否数据一致 */
	@Override
	protected boolean toDataEquals(BaseData data)
	{
		if(!super.toDataEquals(data))
			return false;
		
		PlayerMailData mData=(PlayerMailData)data;
		if(mData.roleData!=null)
		{
			if(this.roleData==null)
				return false;
			if(!this.roleData.dataEquals(mData.roleData))
				return false;
		}
		else
		{
			if(this.roleData!=null)
				return false;
		}
		
		return true;
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("roleData");
		writer.sb.append(':');
		if(this.roleData!=null)
		{
			this.roleData.writeDataString(writer);
		}
		else
		{
			writer.sb.append("RoleShowData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 初始化初值 */
	@Override
	public void initDefault()
	{
		super.initDefault();
		
		this.roleData=new RoleShowData();
		this.roleData.initDefault();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.roleData=null;
	}
	
}
