package com.home.commonBase.data.item.auction;
import com.home.commonBase.constlist.generate.BaseDataType;
import com.home.commonBase.data.item.ItemData;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 拍卖物品数据(generated by shine) */
public class AuctionItemData extends BaseData
{
	/** 数据类型ID */
	public static final int dataID=BaseDataType.AuctionItem;
	
	/** 实例id */
	public long instanceID;
	
	/** 角色ID */
	public long playerID;
	
	/** 物品数据 */
	public ItemData data;
	
	/** 单价 */
	public int price;
	
	/** 上架时间 */
	public long sellTime;
	
	/** 临时时间计次 */
	public int tempTimeIndex;
	
	public AuctionItemData()
	{
		_dataID=BaseDataType.AuctionItem;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "AuctionItemData";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		stream.startReadObj();
		
		this.instanceID=stream.readLong();
		
		this.playerID=stream.readLong();
		
		BaseData dataT=stream.readDataFullNotNull();
		if(dataT!=null)
		{
			if(dataT instanceof ItemData)
			{
				this.data=(ItemData)dataT;
			}
			else
			{
				this.data=new ItemData();
				if(!(dataT.getClass().isAssignableFrom(ItemData.class)))
				{
					stream.throwTypeReadError(ItemData.class,dataT.getClass());
				}
				this.data.shadowCopy(dataT);
			}
		}
		else
		{
			this.data=null;
		}
		
		this.sellTime=stream.readLong();
		
		this.price=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		stream.startWriteObj();
		
		stream.writeLong(this.instanceID);
		
		stream.writeLong(this.playerID);
		
		if(this.data!=null)
		{
			stream.writeDataFullNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
		stream.writeLong(this.sellTime);
		
		stream.writeInt(this.price);
		
		stream.endWriteObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		this.instanceID=stream.readLong();
		
		this.playerID=stream.readLong();
		
		this.data=(ItemData)stream.readDataSimpleNotNull();
		
		this.sellTime=stream.readLong();
		
		this.price=stream.readInt();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		stream.writeLong(this.instanceID);
		
		stream.writeLong(this.playerID);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
		stream.writeLong(this.sellTime);
		
		stream.writeInt(this.price);
		
	}
	
	/** 复制(潜拷贝) */
	@Override
	protected void toShadowCopy(BaseData data)
	{
		if(!(data instanceof AuctionItemData))
			return;
		
		AuctionItemData mData=(AuctionItemData)data;
		
		this.instanceID=mData.instanceID;
		this.playerID=mData.playerID;
		this.data=mData.data;
		this.sellTime=mData.sellTime;
		this.price=mData.price;
	}
	
	/** 复制(深拷贝) */
	@Override
	protected void toCopy(BaseData data)
	{
		if(!(data instanceof AuctionItemData))
			return;
		
		AuctionItemData mData=(AuctionItemData)data;
		
		this.instanceID=mData.instanceID;
		
		this.playerID=mData.playerID;
		
		if(mData.data!=null)
		{
			this.data=(ItemData)mData.data.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
		this.sellTime=mData.sellTime;
		
		this.price=mData.price;
		
	}
	
	/** 是否数据一致 */
	@Override
	protected boolean toDataEquals(BaseData data)
	{
		AuctionItemData mData=(AuctionItemData)data;
		if(this.instanceID!=mData.instanceID)
			return false;
		
		if(this.playerID!=mData.playerID)
			return false;
		
		if(mData.data!=null)
		{
			if(this.data==null)
				return false;
			if(!this.data.dataEquals(mData.data))
				return false;
		}
		else
		{
			if(this.data!=null)
				return false;
		}
		
		if(this.sellTime!=mData.sellTime)
			return false;
		
		if(this.price!=mData.price)
			return false;
		
		return true;
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		writer.writeTabs();
		writer.sb.append("instanceID");
		writer.sb.append(':');
		writer.sb.append(this.instanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("ItemData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("sellTime");
		writer.sb.append(':');
		writer.sb.append(this.sellTime);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("price");
		writer.sb.append(':');
		writer.sb.append(this.price);
		
		writer.writeEnter();
	}
	
	/** 初始化初值 */
	@Override
	public void initDefault()
	{
		this.data=new ItemData();
		this.data.initDefault();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		this.instanceID=0L;
		this.playerID=0L;
		this.data=null;
		this.sellTime=0L;
		this.price=0;
	}
	
}
