package com.home.commonBase.data.item.auction;
import com.home.commonBase.constlist.generate.BaseDataType;
import com.home.commonBase.data.func.FuncInfoLogData;
import com.home.commonBase.data.item.ItemData;
import com.home.commonBase.data.role.RoleSimpleShowData;
import com.home.commonBase.data.system.InfoLogData;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 拍卖行出售物品日志(generated by shine) */
public class AuctionSoldLogData extends FuncInfoLogData
{
	/** 数据类型ID */
	public static final int dataID=BaseDataType.AuctionSoldLog;
	
	/** 物品信息 */
	public ItemData item;
	
	/** 单价 */
	public int price;
	
	/** 购买者信息 */
	public RoleSimpleShowData role;
	
	public AuctionSoldLogData()
	{
		_dataID=BaseDataType.AuctionSoldLog;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "AuctionSoldLogData";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData roleT=stream.readDataFullNotNull();
		if(roleT!=null)
		{
			if(roleT instanceof RoleSimpleShowData)
			{
				this.role=(RoleSimpleShowData)roleT;
			}
			else
			{
				this.role=new RoleSimpleShowData();
				if(!(roleT.getClass().isAssignableFrom(RoleSimpleShowData.class)))
				{
					stream.throwTypeReadError(RoleSimpleShowData.class,roleT.getClass());
				}
				this.role.shadowCopy(roleT);
			}
		}
		else
		{
			this.role=null;
		}
		
		BaseData itemT=stream.readDataFullNotNull();
		if(itemT!=null)
		{
			if(itemT instanceof ItemData)
			{
				this.item=(ItemData)itemT;
			}
			else
			{
				this.item=new ItemData();
				if(!(itemT.getClass().isAssignableFrom(ItemData.class)))
				{
					stream.throwTypeReadError(ItemData.class,itemT.getClass());
				}
				this.item.shadowCopy(itemT);
			}
		}
		else
		{
			this.item=null;
		}
		
		this.price=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.role!=null)
		{
			stream.writeDataFullNotNull(this.role);
		}
		else
		{
			nullObjError("role");
		}
		
		if(this.item!=null)
		{
			stream.writeDataFullNotNull(this.item);
		}
		else
		{
			nullObjError("item");
		}
		
		stream.writeInt(this.price);
		
		stream.endWriteObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.role=(RoleSimpleShowData)stream.readDataSimpleNotNull();
		
		this.item=(ItemData)stream.readDataSimpleNotNull();
		
		this.price=stream.readInt();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.role!=null)
		{
			stream.writeDataSimpleNotNull(this.role);
		}
		else
		{
			nullObjError("role");
		}
		
		if(this.item!=null)
		{
			stream.writeDataSimpleNotNull(this.item);
		}
		else
		{
			nullObjError("item");
		}
		
		stream.writeInt(this.price);
		
	}
	
	/** 复制(潜拷贝) */
	@Override
	protected void toShadowCopy(BaseData data)
	{
		super.toShadowCopy(data);
		
		if(!(data instanceof AuctionSoldLogData))
			return;
		
		AuctionSoldLogData mData=(AuctionSoldLogData)data;
		
		this.role=mData.role;
		this.item=mData.item;
		this.price=mData.price;
	}
	
	/** 复制(深拷贝) */
	@Override
	protected void toCopy(BaseData data)
	{
		super.toCopy(data);
		
		if(!(data instanceof AuctionSoldLogData))
			return;
		
		AuctionSoldLogData mData=(AuctionSoldLogData)data;
		
		if(mData.role!=null)
		{
			this.role=(RoleSimpleShowData)mData.role.clone();
		}
		else
		{
			this.role=null;
			nullObjError("role");
		}
		
		if(mData.item!=null)
		{
			this.item=(ItemData)mData.item.clone();
		}
		else
		{
			this.item=null;
			nullObjError("item");
		}
		
		this.price=mData.price;
		
	}
	
	/** 是否数据一致 */
	@Override
	protected boolean toDataEquals(BaseData data)
	{
		if(!super.toDataEquals(data))
			return false;
		
		AuctionSoldLogData mData=(AuctionSoldLogData)data;
		if(mData.role!=null)
		{
			if(this.role==null)
				return false;
			if(!this.role.dataEquals(mData.role))
				return false;
		}
		else
		{
			if(this.role!=null)
				return false;
		}
		
		if(mData.item!=null)
		{
			if(this.item==null)
				return false;
			if(!this.item.dataEquals(mData.item))
				return false;
		}
		else
		{
			if(this.item!=null)
				return false;
		}
		
		if(this.price!=mData.price)
			return false;
		
		return true;
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("role");
		writer.sb.append(':');
		if(this.role!=null)
		{
			this.role.writeDataString(writer);
		}
		else
		{
			writer.sb.append("RoleSimpleShowData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("item");
		writer.sb.append(':');
		if(this.item!=null)
		{
			this.item.writeDataString(writer);
		}
		else
		{
			writer.sb.append("ItemData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("price");
		writer.sb.append(':');
		writer.sb.append(this.price);
		
		writer.writeEnter();
	}
	
	/** 初始化初值 */
	@Override
	public void initDefault()
	{
		super.initDefault();
		
		this.role=new RoleSimpleShowData();
		this.role.initDefault();
		this.item=new ItemData();
		this.item.initDefault();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.role=null;
		this.item=null;
		this.price=0;
	}
	
}
