package com.home.commonBase.data.scene.scene;
import com.home.commonBase.constlist.generate.BaseDataType;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 副本类场景进入数据(generated by shine) */
public class BattleSceneData extends BaseData
{
	/** 数据类型ID */
	public static final int dataID=BaseDataType.BattleScene;
	
	/** 副本当前状态 */
	public int state;
	
	/** 剩余tick时间(ms)(-1就是没在tick) */
	public int timeTick;
	
	public BattleSceneData()
	{
		_dataID=BaseDataType.BattleScene;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "BattleSceneData";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		stream.startReadObj();
		
		this.state=stream.readInt();
		
		this.timeTick=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		stream.startWriteObj();
		
		stream.writeInt(this.state);
		
		stream.writeInt(this.timeTick);
		
		stream.endWriteObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		this.state=stream.readInt();
		
		this.timeTick=stream.readInt();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		stream.writeInt(this.state);
		
		stream.writeInt(this.timeTick);
		
	}
	
	/** 复制(潜拷贝) */
	@Override
	protected void toShadowCopy(BaseData data)
	{
		if(!(data instanceof BattleSceneData))
			return;
		
		BattleSceneData mData=(BattleSceneData)data;
		
		this.state=mData.state;
		this.timeTick=mData.timeTick;
	}
	
	/** 复制(深拷贝) */
	@Override
	protected void toCopy(BaseData data)
	{
		if(!(data instanceof BattleSceneData))
			return;
		
		BattleSceneData mData=(BattleSceneData)data;
		
		this.state=mData.state;
		
		this.timeTick=mData.timeTick;
		
	}
	
	/** 是否数据一致 */
	@Override
	protected boolean toDataEquals(BaseData data)
	{
		BattleSceneData mData=(BattleSceneData)data;
		if(this.state!=mData.state)
			return false;
		
		if(this.timeTick!=mData.timeTick)
			return false;
		
		return true;
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		writer.writeTabs();
		writer.sb.append("state");
		writer.sb.append(':');
		writer.sb.append(this.state);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("timeTick");
		writer.sb.append(':');
		writer.sb.append(this.timeTick);
		
		writer.writeEnter();
	}
	
	/** 初始化初值 */
	@Override
	public void initDefault()
	{
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		this.state=0;
		this.timeTick=0;
	}
	
}
