package com.home.commonBase.data.scene.unit.identity;
import com.home.commonBase.config.game.BuildingConfig;
import com.home.commonBase.constlist.generate.BaseDataType;
import com.home.commonBase.data.scene.unit.identity.FightUnitIdentityData;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 建筑身份数据(generated by shine) */
public class BuildingIdentityData extends FightUnitIdentityData
{
	/** 数据类型ID */
	public static final int dataID=BaseDataType.BuildingIdentity;
	
	/** 建筑状态(见BuildingStateType) */
	public int state;
	
	/** 建造/升级 经过时间 */
	public int passTime;
	
	public BuildingIdentityData()
	{
		_dataID=BaseDataType.BuildingIdentity;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "BuildingIdentityData";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.state=stream.readInt();
		
		this.passTime=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.state);
		
		stream.writeInt(this.passTime);
		
		stream.endWriteObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.state=stream.readInt();
		
		this.passTime=stream.readInt();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.state);
		
		stream.writeInt(this.passTime);
		
	}
	
	/** 复制(潜拷贝) */
	@Override
	protected void toShadowCopy(BaseData data)
	{
		super.toShadowCopy(data);
		
		if(!(data instanceof BuildingIdentityData))
			return;
		
		BuildingIdentityData mData=(BuildingIdentityData)data;
		
		this.state=mData.state;
		this.passTime=mData.passTime;
	}
	
	/** 复制(深拷贝) */
	@Override
	protected void toCopy(BaseData data)
	{
		super.toCopy(data);
		
		if(!(data instanceof BuildingIdentityData))
			return;
		
		BuildingIdentityData mData=(BuildingIdentityData)data;
		
		this.state=mData.state;
		
		this.passTime=mData.passTime;
		
	}
	
	/** 是否数据一致 */
	@Override
	protected boolean toDataEquals(BaseData data)
	{
		if(!super.toDataEquals(data))
			return false;
		
		BuildingIdentityData mData=(BuildingIdentityData)data;
		if(this.state!=mData.state)
			return false;
		
		if(this.passTime!=mData.passTime)
			return false;
		
		return true;
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("state");
		writer.sb.append(':');
		writer.sb.append(this.state);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("passTime");
		writer.sb.append(':');
		writer.sb.append(this.passTime);
		
		writer.writeEnter();
	}
	
	/** 初始化初值 */
	@Override
	public void initDefault()
	{
		super.initDefault();
		
	}
	
	/** 获取战斗单位ID */
	public int getFightUnitID()
	{
		return BuildingConfig.get(id).fightUnitID;
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.state=0;
		this.passTime=0;
	}
	
}
