package com.home.commonBase.data.scene.unit.identity;
import com.home.commonBase.config.game.PetConfig;
import com.home.commonBase.constlist.generate.BaseDataType;
import com.home.commonBase.data.scene.unit.identity.FightUnitIdentityData;
import com.home.commonBase.data.scene.unit.identity.MUnitIdentityData;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.data.BaseData;

/** 宠物身份数据(generated by shine) */
public class PetIdentityData extends MUnitIdentityData
{
	/** 数据类型ID */
	public static final int dataID=BaseDataType.PetIdentity;
	
	public PetIdentityData()
	{
		_dataID=BaseDataType.PetIdentity;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "PetIdentityData";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		stream.endReadObj();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.endWriteObj();
	}
	
	@Override
	public int getFightUnitID()
	{
		return PetConfig.get(id).fightUnitID;
	}
	
}
