package com.home.commonBase.data.system;
import com.home.commonBase.constlist.generate.BaseDataType;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 区服客户端数据(generated by shine) */
public class AreaClientData extends BaseData
{
	/** 数据类型ID */
	public static final int dataID=BaseDataType.AreaClient;
	
	/** 游戏服ID */
	public int areaID;
	
	/** 名字 */
	public String name="";
	
	/** 存在标记 */
	public boolean mark;
	
	/** 负载值 */
	public int load=0;
	
	public AreaClientData()
	{
		_dataID=BaseDataType.AreaClient;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "AreaClientData";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		stream.startReadObj();
		
		this.areaID=stream.readInt();
		
		this.name=stream.readUTF();
		
		this.load=stream.readInt();
		
		this.mark=stream.readBoolean();
		
		stream.endReadObj();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		stream.startWriteObj();
		
		stream.writeInt(this.areaID);
		
		stream.writeUTF(this.name);
		
		stream.writeInt(this.load);
		
		stream.writeBoolean(this.mark);
		
		stream.endWriteObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		this.areaID=stream.readInt();
		
		this.name=stream.readUTF();
		
		this.load=stream.readInt();
		
		this.mark=stream.readBoolean();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		stream.writeInt(this.areaID);
		
		stream.writeUTF(this.name);
		
		stream.writeInt(this.load);
		
		stream.writeBoolean(this.mark);
		
	}
	
	/** 复制(潜拷贝) */
	@Override
	protected void toShadowCopy(BaseData data)
	{
		if(!(data instanceof AreaClientData))
			return;
		
		AreaClientData mData=(AreaClientData)data;
		
		this.areaID=mData.areaID;
		this.name=mData.name;
		this.load=mData.load;
		this.mark=mData.mark;
	}
	
	/** 复制(深拷贝) */
	@Override
	protected void toCopy(BaseData data)
	{
		if(!(data instanceof AreaClientData))
			return;
		
		AreaClientData mData=(AreaClientData)data;
		
		this.areaID=mData.areaID;
		
		this.name=mData.name;
		
		this.load=mData.load;
		
		this.mark=mData.mark;
		
	}
	
	/** 是否数据一致 */
	@Override
	protected boolean toDataEquals(BaseData data)
	{
		AreaClientData mData=(AreaClientData)data;
		if(this.areaID!=mData.areaID)
			return false;
		
		if(!this.name.equals(mData.name))
			return false;
		
		if(this.load!=mData.load)
			return false;
		
		if(this.mark!=mData.mark)
			return false;
		
		return true;
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		writer.writeTabs();
		writer.sb.append("areaID");
		writer.sb.append(':');
		writer.sb.append(this.areaID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("name");
		writer.sb.append(':');
		writer.sb.append(this.name);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("load");
		writer.sb.append(':');
		writer.sb.append(this.load);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("mark");
		writer.sb.append(':');
		writer.sb.append(this.mark);
		
		writer.writeEnter();
	}
	
	/** 初始化初值 */
	@Override
	public void initDefault()
	{
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		this.areaID=0;
		this.name="";
		this.load=0;
		this.mark=false;
	}
	
}
