package com.home.commonBase.data.system;
import com.home.commonBase.constlist.generate.BaseDataType;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 客户端离线事务数据(generated by shine) */
public class ClientOfflineWorkData extends BaseData
{
	/** 时间戳 */
	public long workTime;
	
	/** 序号 */
	public int workIndex;
	
	/** 数据类型ID */
	public static final int dataID=BaseDataType.ClientOfflineWork;
	
	public ClientOfflineWorkData()
	{
		_dataID=BaseDataType.ClientOfflineWork;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ClientOfflineWorkData";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		stream.startReadObj();
		
		this.workIndex=stream.readInt();
		
		this.workTime=stream.readLong();
		
		stream.endReadObj();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		stream.startWriteObj();
		
		stream.writeInt(this.workIndex);
		
		stream.writeLong(this.workTime);
		
		stream.endWriteObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		this.workIndex=stream.readInt();
		
		this.workTime=stream.readLong();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		stream.writeInt(this.workIndex);
		
		stream.writeLong(this.workTime);
		
	}
	
	/** 复制(潜拷贝) */
	@Override
	protected void toShadowCopy(BaseData data)
	{
		if(!(data instanceof ClientOfflineWorkData))
			return;
		
		ClientOfflineWorkData mData=(ClientOfflineWorkData)data;
		
		this.workIndex=mData.workIndex;
		this.workTime=mData.workTime;
	}
	
	/** 复制(深拷贝) */
	@Override
	protected void toCopy(BaseData data)
	{
		if(!(data instanceof ClientOfflineWorkData))
			return;
		
		ClientOfflineWorkData mData=(ClientOfflineWorkData)data;
		
		this.workIndex=mData.workIndex;
		
		this.workTime=mData.workTime;
		
	}
	
	/** 是否数据一致 */
	@Override
	protected boolean toDataEquals(BaseData data)
	{
		ClientOfflineWorkData mData=(ClientOfflineWorkData)data;
		if(this.workIndex!=mData.workIndex)
			return false;
		
		if(this.workTime!=mData.workTime)
			return false;
		
		return true;
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		writer.writeTabs();
		writer.sb.append("workIndex");
		writer.sb.append(':');
		writer.sb.append(this.workIndex);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("workTime");
		writer.sb.append(':');
		writer.sb.append(this.workTime);
		
		writer.writeEnter();
	}
	
	/** 初始化初值 */
	@Override
	public void initDefault()
	{
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		this.workIndex=0;
		this.workTime=0L;
	}
	
}
