package com.home.commonBase.data.system;
import com.home.commonBase.constlist.generate.BaseDataType;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 计数数据(generated by shine) */
public class CountData extends BaseData
{
	/** 数据类型ID */
	public static final int dataID=BaseDataType.Count;
	
	/** 数目 */
	public int num;
	
	/** 总数 */
	public long total;
	
	public CountData()
	{
		_dataID=BaseDataType.Count;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CountData";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		stream.startReadObj();
		
		this.num=stream.readInt();
		
		this.total=stream.readLong();
		
		stream.endReadObj();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		stream.startWriteObj();
		
		stream.writeInt(this.num);
		
		stream.writeLong(this.total);
		
		stream.endWriteObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		this.num=stream.readInt();
		
		this.total=stream.readLong();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		stream.writeInt(this.num);
		
		stream.writeLong(this.total);
		
	}
	
	/** 复制(潜拷贝) */
	@Override
	protected void toShadowCopy(BaseData data)
	{
		if(!(data instanceof CountData))
			return;
		
		CountData mData=(CountData)data;
		
		this.num=mData.num;
		this.total=mData.total;
	}
	
	/** 复制(深拷贝) */
	@Override
	protected void toCopy(BaseData data)
	{
		if(!(data instanceof CountData))
			return;
		
		CountData mData=(CountData)data;
		
		this.num=mData.num;
		
		this.total=mData.total;
		
	}
	
	/** 是否数据一致 */
	@Override
	protected boolean toDataEquals(BaseData data)
	{
		CountData mData=(CountData)data;
		if(this.num!=mData.num)
			return false;
		
		if(this.total!=mData.total)
			return false;
		
		return true;
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		writer.writeTabs();
		writer.sb.append("num");
		writer.sb.append(':');
		writer.sb.append(this.num);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("total");
		writer.sb.append(':');
		writer.sb.append(this.total);
		
		writer.writeEnter();
	}
	
	/** 初始化初值 */
	@Override
	public void initDefault()
	{
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		this.num=0;
		this.total=0L;
	}
	
}
