package com.home.commonBase.data.system;
import com.home.commonBase.constlist.generate.BaseDataType;
import com.home.commonBase.global.CommonSetting;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 存库数据版本(generated by shine) */
public class SaveVersionData extends BaseData
{
	/** 数据类型ID */
	public static final int dataID=BaseDataType.SaveVersion;
	
	/** c层数据版本号 */
	public int cVersion;
	
	/** g层版本号(数据) */
	public int gVersion;
	
	public SaveVersionData()
	{
		_dataID=BaseDataType.SaveVersion;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SaveVersionData";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		stream.startReadObj();
		
		this.cVersion=stream.readInt();
		
		this.gVersion=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		stream.startWriteObj();
		
		stream.writeInt(this.cVersion);
		
		stream.writeInt(this.gVersion);
		
		stream.endWriteObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		this.cVersion=stream.readInt();
		
		this.gVersion=stream.readInt();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		stream.writeInt(this.cVersion);
		
		stream.writeInt(this.gVersion);
		
	}
	
	/** 复制(潜拷贝) */
	@Override
	protected void toShadowCopy(BaseData data)
	{
		if(!(data instanceof SaveVersionData))
			return;
		
		SaveVersionData mData=(SaveVersionData)data;
		
		this.cVersion=mData.cVersion;
		this.gVersion=mData.gVersion;
	}
	
	/** 复制(深拷贝) */
	@Override
	protected void toCopy(BaseData data)
	{
		if(!(data instanceof SaveVersionData))
			return;
		
		SaveVersionData mData=(SaveVersionData)data;
		
		this.cVersion=mData.cVersion;
		
		this.gVersion=mData.gVersion;
		
	}
	
	/** 是否数据一致 */
	@Override
	protected boolean toDataEquals(BaseData data)
	{
		SaveVersionData mData=(SaveVersionData)data;
		if(this.cVersion!=mData.cVersion)
			return false;
		
		if(this.gVersion!=mData.gVersion)
			return false;
		
		return true;
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		writer.writeTabs();
		writer.sb.append("cVersion");
		writer.sb.append(':');
		writer.sb.append(this.cVersion);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("gVersion");
		writer.sb.append(':');
		writer.sb.append(this.gVersion);
		
		writer.writeEnter();
	}
	
	/** 初始化初值 */
	@Override
	public void initDefault()
	{
		
	}
	
	/** 设置为现在的版本 */
	public void setNowVersion()
	{
		cVersion=CommonSetting.cVersion;
		gVersion=CommonSetting.gVersion;
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		this.cVersion=0;
		this.gVersion=0;
	}
	
}
