package com.home.commonBase.data.system;
import com.home.commonBase.constlist.generate.BaseDataType;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 事务完成数据(generated by shine) */
public class WorkCompleteData extends BaseData
{
	/** 数据类型ID */
	public static final int dataID=BaseDataType.WorkComplete;
	
	/** 实例ID */
	public long workInstanceID;
	
	/** 发起者索引(type+id) */
	public int senderIndex;
	
	public WorkCompleteData()
	{
		_dataID=BaseDataType.WorkComplete;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "WorkCompleteData";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		stream.startReadObj();
		
		this.workInstanceID=stream.readLong();
		
		this.senderIndex=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		stream.startWriteObj();
		
		stream.writeLong(this.workInstanceID);
		
		stream.writeInt(this.senderIndex);
		
		stream.endWriteObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		this.workInstanceID=stream.readLong();
		
		this.senderIndex=stream.readInt();
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		stream.writeLong(this.workInstanceID);
		
		stream.writeInt(this.senderIndex);
		
	}
	
	/** 复制(潜拷贝) */
	@Override
	protected void toShadowCopy(BaseData data)
	{
		if(!(data instanceof WorkCompleteData))
			return;
		
		WorkCompleteData mData=(WorkCompleteData)data;
		
		this.workInstanceID=mData.workInstanceID;
		this.senderIndex=mData.senderIndex;
	}
	
	/** 复制(深拷贝) */
	@Override
	protected void toCopy(BaseData data)
	{
		if(!(data instanceof WorkCompleteData))
			return;
		
		WorkCompleteData mData=(WorkCompleteData)data;
		
		this.workInstanceID=mData.workInstanceID;
		
		this.senderIndex=mData.senderIndex;
		
	}
	
	/** 是否数据一致 */
	@Override
	protected boolean toDataEquals(BaseData data)
	{
		WorkCompleteData mData=(WorkCompleteData)data;
		if(this.workInstanceID!=mData.workInstanceID)
			return false;
		
		if(this.senderIndex!=mData.senderIndex)
			return false;
		
		return true;
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		writer.writeTabs();
		writer.sb.append("workInstanceID");
		writer.sb.append(':');
		writer.sb.append(this.workInstanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("senderIndex");
		writer.sb.append(':');
		writer.sb.append(this.senderIndex);
		
		writer.writeEnter();
	}
	
	/** 初始化初值 */
	@Override
	public void initDefault()
	{
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		this.workInstanceID=0L;
		this.senderIndex=0;
	}
	
}
