package com.home.commonBase.table.task;
import com.home.commonBase.table.result.GlobalDBResult;
import com.home.commonBase.table.table.GlobalTable;
import com.home.shine.data.DateData;
import com.home.shine.table.BaseDBResult;
import com.home.shine.table.BaseTable;
import com.home.shine.table.task.BaseDBTask;

/** 全局表(generated by shine) */
public class GlobalDBTask extends BaseDBTask
{
	/** 默认主键 */
	public int key;
	
	/** 数据结构版本 */
	public int dataVersion;
	
	/** 上次保存时间 */
	public DateData saveDate;
	
	/** blob值 */
	public byte[] data;
	
	@Override
	public BaseTable createTable()
	{
		return new GlobalTable();
	}
	
	@Override
	public BaseDBResult createResult()
	{
		return new GlobalDBResult();
	}
	
	@Override
	protected void toSetValues(boolean isInsert)
	{
		setInt(key);
		setInt(dataVersion);
		setDate(saveDate);
		setBytes(data);
	}
	
	@Override
	public Object getPrimaryKeyByIndex(int index)
	{
		switch(index)
		{
			case 0:
				return key;
		}
		return null;
	}
	
	@Override
	protected void toSetPrimaryKeyByIndex(int index)
	{
		switch(index)
		{
			case 0:
			{
				setInt(key);
			}
				break;
		}
	}
	
}
