package com.home.commonBase.table.task;
import com.home.commonBase.table.result.PlayerDBResult;
import com.home.commonBase.table.table.PlayerTable;
import com.home.shine.data.DateData;
import com.home.shine.table.BaseDBResult;
import com.home.shine.table.BaseTable;
import com.home.shine.table.task.BaseDBTask;

/** 角色表(generated by shine) */
public class PlayerDBTask extends BaseDBTask
{
	/** 角色id */
	public long playerID;
	
	/** 名字 */
	public String name="";
	
	/** userID */
	public long userID;
	
	/** uid */
	public String uid="";
	
	/** 原区ID */
	public int createAreaID;
	
	/** 平台 */
	public String platform="";
	
	/** source版本 */
	public int sourceVersion;
	
	/** app版本 */
	public int appVersion;
	
	/** 创建日期 */
	public DateData createDate;
	
	/** 上次保存时间 */
	public DateData saveDate;
	
	/** 登录数据 */
	public byte[] loginData;
	
	/** 数据 */
	public byte[] data;
	
	/** 离线事务数据 */
	public byte[] offlineData;
	
	public PlayerDBTask()
	{
		
	}
	
	@Override
	public BaseTable createTable()
	{
		return new PlayerTable();
	}
	
	@Override
	public BaseDBResult createResult()
	{
		return new PlayerDBResult();
	}
	
	@Override
	public Object getPrimaryKeyByIndex(int index)
	{
		switch(index)
		{
			case 0:
				return playerID;
			case 1:
				return name;
			case 3:
				return uid;
		}
		return null;
	}
	
	@Override
	protected void toSetPrimaryKeyByIndex(int index)
	{
		switch(index)
		{
			case 0:
			{
				setLong(playerID);
			}
				break;
			case 1:
			{
				setString(name);
			}
				break;
			case 2:
			{
				setLong(userID);
				setInt(createAreaID);
			}
				break;
			case 3:
			{
				setString(uid);
			}
				break;
		}
	}
	
	@Override
	protected void toSetValues(boolean isInsert)
	{
		setLong(playerID);
		setString(name);
		setLong(userID);
		setInt(createAreaID);
		setString(uid);
		setString(platform);
		setInt(appVersion);
		setInt(sourceVersion);
		setDate(createDate);
		setDate(saveDate);
		setBytes(loginData);
		setBytes(data);
		setBytes(offlineData);
	}
	
}
