package com.home.commonBase.table.task;
import com.home.commonBase.table.result.UserDBResult;
import com.home.commonBase.table.table.UserTable;
import com.home.shine.table.BaseDBResult;
import com.home.shine.table.BaseTable;
import com.home.shine.table.task.BaseDBTask;

/** 用户表(generated by shine) */
public class UserDBTask extends BaseDBTask
{
	/** 用户ID */
	public long userID;
	
	/** platform_uid */
	public String puid="";
	
	/** 平台类型 */
	public String platform="";
	
	/** 是否成人 */
	public boolean isAdult;
	
	/** 绑定(上次选择)区服ID */
	public int areaID;
	
	/** 拥有角色ID组(逗号分隔) */
	public String playerIDs="";
	
	/** 指向源userID(一账号多平台用) */
	public long sourceUserID;
	
	@Override
	public BaseTable createTable()
	{
		return new UserTable();
	}
	
	@Override
	public BaseDBResult createResult()
	{
		return new UserDBResult();
	}
	
	@Override
	public Object getPrimaryKeyByIndex(int index)
	{
		switch(index)
		{
			case 0:
				return userID;
			case 1:
				return puid;
		}
		return null;
	}
	
	@Override
	protected void toSetPrimaryKeyByIndex(int index)
	{
		switch(index)
		{
			case 0:
			{
				setLong(userID);
			}
				break;
			case 1:
			{
				setString(puid);
			}
				break;
		}
	}
	
	@Override
	protected void toSetValues(boolean isInsert)
	{
		setLong(userID);
		setString(puid);
		setString(platform);
		setBoolean(isAdult);
		setInt(areaID);
		setString(playerIDs);
		setLong(sourceUserID);
	}
	
}
