package com.home.commonCenter.control;
import com.home.commonBase.data.login.PlayerDeletedToCenterWData;
import com.home.commonBase.data.social.roleGroup.work.CreateRoleGroupToCenterWData;
import com.home.commonBase.data.social.roleGroup.work.RemoveRoleGroupToCenterWData;
import com.home.commonBase.data.system.CenterGlobalWorkData;
import com.home.commonCenter.global.CenterC;

/** 中心服事务控制(generated by shine) */
public class CenterWorkControl extends BaseCenterWorkControl
{
	protected void regist()
	{
		registOne(PlayerDeletedToCenterWData.dataID,this::doPlayerDeletedToCenterW);
		registOne(CreateRoleGroupToCenterWData.dataID,this::doCreateRoleGroupToCenterW);
		registOne(RemoveRoleGroupToCenterWData.dataID,this::doRemoveRoleGroupToCenterW);
	}
	
	/** 创建玩家群到中心服事务 */
	private void doCreateRoleGroupToCenterW(CenterGlobalWorkData wData)
	{
		CreateRoleGroupToCenterWData data=(CreateRoleGroupToCenterWData)wData;
		
		CenterC.global.func.getRoleGroupTool(data.funcID).addRoleGroupSimpleData(data.simpleData);
	}
	
	/** 移除玩家群到中心服事务 */
	private void doRemoveRoleGroupToCenterW(CenterGlobalWorkData wData)
	{
		RemoveRoleGroupToCenterWData data=(RemoveRoleGroupToCenterWData)wData;
		
		CenterC.global.func.getRoleGroupTool(data.funcID).removeRoleGroup(data.groupID);
	}
	
	/** 玩家删除到中心服消息 */
	private void doPlayerDeletedToCenterW(CenterGlobalWorkData wData)
	{
		PlayerDeletedToCenterWData data=(PlayerDeletedToCenterWData)wData;
		
		CenterC.global.onPlayerDelete(data.playerID);
	}
	
}
