package com.home.commonCenter.net.centerRequest.system;
import com.home.commonCenter.constlist.generate.CenterRequestType;
import com.home.commonCenter.net.base.CenterRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送中心服消息回执(generated by shine) */
public class SendCenterReceiptToClientRequest extends CenterRequest
{
	/** 数据类型ID */
	public static final int dataID=CenterRequestType.SendCenterReceiptToClient;
	
	/** 对应的requestID */
	public int requestID;
	
	public SendCenterReceiptToClientRequest()
	{
		_dataID=CenterRequestType.SendCenterReceiptToClient;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendCenterReceiptToClientRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.requestID);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.requestID);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("requestID");
		writer.sb.append(':');
		writer.sb.append(this.requestID);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.requestID=0;
	}
	
	/** 创建实例 */
	public static SendCenterReceiptToClientRequest create(int requestID)
	{
		SendCenterReceiptToClientRequest re=(SendCenterReceiptToClientRequest)BytesControl.createRequest(dataID);
		re.requestID=requestID;
		return re;
	}
	
}
