package com.home.commonCenter.net.centerRequest.system;
import com.home.commonCenter.constlist.generate.CenterRequestType;
import com.home.commonCenter.net.base.CenterRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 中心服推送信息码(generated by shine) */
public class SendInfoCodeFromCenterRequest extends CenterRequest
{
	/** 数据类型ID */
	public static final int dataID=CenterRequestType.SendInfoCodeFromCenter;
	
	/** 错误码 */
	public int code;
	
	public SendInfoCodeFromCenterRequest()
	{
		_dataID=CenterRequestType.SendInfoCodeFromCenter;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendInfoCodeFromCenterRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("code");
		writer.sb.append(':');
		writer.sb.append(this.code);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.code);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.code);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.code=0;
	}
	
	/** 创建实例 */
	public static SendInfoCodeFromCenterRequest create(int code)
	{
		SendInfoCodeFromCenterRequest re=(SendInfoCodeFromCenterRequest)BytesControl.createRequest(dataID);
		re.code=code;
		return re;
	}
	
}
