package com.home.commonCenter.net.serverRequest.game.activity;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonCenter.net.serverRequest.game.base.CenterToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 改变活动强制关闭消息(generated by shine) */
public class ChangeActivityForceCloseToGameServerRequest extends CenterToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.ChangeActivityForceCloseToGame;
	
	public int id;
	
	/** 是否强制关闭 */
	public boolean isForceClosed;
	
	public ChangeActivityForceCloseToGameServerRequest()
	{
		_dataID=ServerMessageType.ChangeActivityForceCloseToGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeBoolean(this.isForceClosed);
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ChangeActivityForceCloseToGameServerRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("id");
		writer.sb.append(':');
		writer.sb.append(this.id);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("isForceClosed");
		writer.sb.append(':');
		writer.sb.append(this.isForceClosed);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.id=0;
		this.isForceClosed=false;
	}
	
	/** 创建实例 */
	public static ChangeActivityForceCloseToGameServerRequest create(int id,boolean isForceClosed)
	{
		ChangeActivityForceCloseToGameServerRequest re=(ChangeActivityForceCloseToGameServerRequest)BytesControl.createRequest(dataID);
		re.id=id;
		re.isForceClosed=isForceClosed;
		return re;
	}
	
}
