package com.home.commonCenter.net.serverRequest.game.func.rank.subsection;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonCenter.net.serverRequest.game.func.base.FuncPlayerToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 刷新subIndex(generated by shine) */
public class FuncRefreshSubsectionIndexToGameServerRequest extends FuncPlayerToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncRefreshSubsectionIndexToGame;
	
	/** 大组index */
	public int subsectionIndex;
	
	/** 小组index */
	public int subsectionSubIndex;
	
	public FuncRefreshSubsectionIndexToGameServerRequest()
	{
		_dataID=ServerMessageType.FuncRefreshSubsectionIndexToGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncRefreshSubsectionIndexToGameServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.subsectionIndex);
		
		stream.writeInt(this.subsectionSubIndex);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("subsectionIndex");
		writer.sb.append(':');
		writer.sb.append(this.subsectionIndex);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("subsectionSubIndex");
		writer.sb.append(':');
		writer.sb.append(this.subsectionSubIndex);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.subsectionIndex=0;
		this.subsectionSubIndex=0;
	}
	
	/** 创建实例 */
	public static FuncRefreshSubsectionIndexToGameServerRequest create(int funcID,int subsectionIndex,int subsectionSubIndex)
	{
		FuncRefreshSubsectionIndexToGameServerRequest re=(FuncRefreshSubsectionIndexToGameServerRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.subsectionIndex=subsectionIndex;
		re.subsectionSubIndex=subsectionSubIndex;
		return re;
	}
	
}
