package com.home.commonCenter.net.serverRequest.game.mail;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.mail.MailData;
import com.home.commonCenter.net.serverRequest.game.base.CenterToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 发送邮件到game服(generated by shine) */
public class SendMailToGameServerRequest extends CenterToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.SendMailToGame;
	
	/** 邮件数据 */
	public MailData data;
	
	public SendMailToGameServerRequest()
	{
		_dataID=ServerMessageType.SendMailToGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		MailData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(MailData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendMailToGameServerRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("MailData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static SendMailToGameServerRequest create(MailData data)
	{
		SendMailToGameServerRequest re=(SendMailToGameServerRequest)BytesControl.createRequest(dataID);
		re.data=data;
		return re;
	}
	
}
