package com.home.commonCenter.net.serverRequest.game.scene;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.scene.scene.SceneEnterArgData;
import com.home.commonBase.data.scene.scene.SceneLocationData;
import com.home.commonCenter.net.serverRequest.game.base.PlayerToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 进入指定场景到game(generated by shine) */
public class EnterSignedSceneToGameServerRequest extends PlayerToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.EnterSignedSceneToGame;
	
	/** 场景位置数据 */
	public SceneLocationData data;
	
	public EnterSignedSceneToGameServerRequest()
	{
		_dataID=ServerMessageType.EnterSignedSceneToGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		SceneLocationData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(SceneLocationData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "EnterSignedSceneToGameServerRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("SceneLocationData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static EnterSignedSceneToGameServerRequest create(SceneLocationData data)
	{
		EnterSignedSceneToGameServerRequest re=(EnterSignedSceneToGameServerRequest)BytesControl.createRequest(dataID);
		re.data=data;
		return re;
	}
	
}
