package com.home.commonCenter.net.serverRequest.game.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonCenter.net.serverRequest.game.base.CenterToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 刷新服务器偏移时间到游戏服(generated by shine) */
public class RefreshServerOffTimeToGameServerRequest extends CenterToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.RefreshServerOffTimeToGame;
	
	/** 偏移时间 */
	public long offTime;
	
	public RefreshServerOffTimeToGameServerRequest()
	{
		_dataID=ServerMessageType.RefreshServerOffTimeToGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.offTime);
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "RefreshServerOffTimeToGameServerRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("offTime");
		writer.sb.append(':');
		writer.sb.append(this.offTime);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.offTime=0L;
	}
	
	/** 创建实例 */
	public static RefreshServerOffTimeToGameServerRequest create(long offTime)
	{
		RefreshServerOffTimeToGameServerRequest re=(RefreshServerOffTimeToGameServerRequest)BytesControl.createRequest(dataID);
		re.offTime=offTime;
		return re;
	}
	
}
