package com.home.commonCenter.net.serverRequest.game.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.system.PlayerWorkCompleteData;
import com.home.commonCenter.net.serverRequest.game.base.CenterToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 发送角色事务完成消息(generated by shine) */
public class SendPlayerWorkCompleteServerRequest extends CenterToGameServerRequest
{
	/** 事务完成数据 */
	public PlayerWorkCompleteData data;
	
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.SendPlayerWorkComplete;
	
	public SendPlayerWorkCompleteServerRequest()
	{
		_dataID=ServerMessageType.SendPlayerWorkComplete;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		PlayerWorkCompleteData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(PlayerWorkCompleteData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendPlayerWorkCompleteServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("PlayerWorkCompleteData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static SendPlayerWorkCompleteServerRequest create(PlayerWorkCompleteData data)
	{
		SendPlayerWorkCompleteServerRequest re=(SendPlayerWorkCompleteServerRequest)BytesControl.createRequest(dataID);
		re.data=data;
		return re;
	}
	
}
