package com.home.commonCenter.net.serverRequest.manager;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonCenter.net.serverRequest.manager.base.CenterToManagerServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 回复添加角色事务到中心服(generated by shine) */
public class ReMAddPlayerWorkToCenterServerRequest extends CenterToManagerServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.ReMAddPlayerWorkToCenter;
	
	/** 结果 */
	public int result;
	
	public int httpID;
	
	public ReMAddPlayerWorkToCenterServerRequest()
	{
		_dataID=ServerMessageType.ReMAddPlayerWorkToCenter;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ReMAddPlayerWorkToCenterServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.httpID);
		
		stream.writeInt(this.result);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("httpID");
		writer.sb.append(':');
		writer.sb.append(this.httpID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("result");
		writer.sb.append(':');
		writer.sb.append(this.result);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.httpID=0;
		this.result=0;
	}
	
	/** 创建实例 */
	public static ReMAddPlayerWorkToCenterServerRequest create(int httpID,int result)
	{
		ReMAddPlayerWorkToCenterServerRequest re=(ReMAddPlayerWorkToCenterServerRequest)BytesControl.createRequest(dataID);
		re.httpID=httpID;
		re.result=result;
		return re;
	}
	
}
