package com.home.commonCenter.net.serverResponse.game.func.auction;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonCenter.global.CenterC;
import com.home.commonCenter.net.serverResponse.game.func.base.FuncPlayerToCenterServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.constlist.ThreadType;
import com.home.shine.control.ThreadControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 取消上架物品到拍卖行(generated by shine) */
public class FuncSendAuctionCancelSellItemToCenterServerResponse extends FuncPlayerToCenterServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncSendAuctionCancelSellItemToCenter;
	
	public int reason;
	
	public long instanceID;
	
	public FuncSendAuctionCancelSellItemToCenterServerResponse()
	{
		_dataID=ServerMessageType.FuncSendAuctionCancelSellItemToCenter;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncSendAuctionCancelSellItemToCenterServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.instanceID=stream.readLong();
		
		this.reason=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("instanceID");
		writer.sb.append(':');
		writer.sb.append(this.instanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("reason");
		writer.sb.append(':');
		writer.sb.append(this.reason);
		
		writer.writeEnter();
	}
	
	@Override
	public void dispatch()
	{
		_threadType=ThreadType.Pool;
		_poolIndex=(int)instanceID & ThreadControl.poolThreadNumMark;
		doDispatch();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		CenterC.global.func.getAuctionTool(funcID).getExecutor(_poolIndex).removeSellItem(playerID,instanceID,reason);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.instanceID=0L;
		this.reason=0;
	}
	
}
