package com.home.commonCenter.net.serverResponse.game.scene;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.scene.scene.SceneEnterArgData;
import com.home.commonBase.data.scene.scene.SceneLocationData;
import com.home.commonCenter.global.CenterC;
import com.home.commonCenter.net.serverResponse.game.base.GameToCenterServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 回创建指定场景到中心服(generated by shine) */
public class ReCreateSignedSceneToCenterServerResponse extends GameToCenterServerResponse
{
	/** 序号 */
	public int index;
	
	/** 进入场景参数数据 */
	public SceneLocationData eData;
	
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.ReCreateSignedSceneToCenter;
	
	public ReCreateSignedSceneToCenterServerResponse()
	{
		_dataID=ServerMessageType.ReCreateSignedSceneToCenter;
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.index=stream.readInt();
		
		this.eData=(SceneLocationData)stream.readDataSimpleNotNull();
		
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		CenterC.scene.reCreateSignedScene(index,eData);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ReCreateSignedSceneToCenterServerResponse";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("index");
		writer.sb.append(':');
		writer.sb.append(this.index);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("eData");
		writer.sb.append(':');
		if(this.eData!=null)
		{
			this.eData.writeDataString(writer);
		}
		else
		{
			writer.sb.append("SceneLocationData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.index=0;
		this.eData=null;
	}
	
}
