package com.home.commonCenter.net.serverResponse.game.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonCenter.global.CenterC;
import com.home.commonCenter.net.serverResponse.game.base.PlayerToCenterServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 客户端GM指令到中心服(generated by shine) */
public class ClientGMToCenterServerResponse extends PlayerToCenterServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.ClientGMToCenter;
	
	/** 指令 */
	public String cmd="";
	
	public ClientGMToCenterServerResponse()
	{
		_dataID=ServerMessageType.ClientGMToCenter;
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.cmd=stream.readUTF();
		
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		CenterC.clientGM.execute(playerID,cmd);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ClientGMToCenterServerResponse";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("cmd");
		writer.sb.append(':');
		writer.sb.append(this.cmd);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.cmd="";
	}
	
}
