package com.home.commonCenter.net.serverResponse.manager;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.system.PlayerWorkData;
import com.home.commonCenter.global.CenterC;
import com.home.commonCenter.net.base.CenterServerResponse;
import com.home.commonCenter.net.serverResponse.manager.base.ManagerToCenterServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 执行查询事务到中心服(generated by shine) */
public class MQueryPlayerWorkToCenterServerResponse extends ManagerToCenterServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.MQueryPlayerWorkToCenter;
	
	public int httpID;
	
	/** 数据 */
	public PlayerWorkData data;
	
	public MQueryPlayerWorkToCenterServerResponse()
	{
		_dataID=ServerMessageType.MQueryPlayerWorkToCenter;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "MQueryPlayerWorkToCenterServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.httpID=stream.readInt();
		
		this.data=(PlayerWorkData)stream.readDataSimpleNotNull();
		
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		CenterC.main.addPlayerQueryWorkByGM(httpID,data);
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("httpID");
		writer.sb.append(':');
		writer.sb.append(this.httpID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("PlayerWorkData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.httpID=0;
		this.data=null;
	}
	
}
