package com.home.commonCenter.net.serverResponse.manager;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.constlist.system.ManagerCommandType;
import com.home.commonCenter.global.CenterC;
import com.home.commonCenter.net.serverResponse.manager.base.ManagerToCenterServerResponse;
import com.home.shine.agent.AgentControl;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** manager到中心服指令(generated by shine) */
public class ManagerToCenterCommandServerResponse extends ManagerToCenterServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.ManagerToCenterCommand;
	
	public int type;
	
	public ManagerToCenterCommandServerResponse()
	{
		_dataID=ServerMessageType.ManagerToCenterCommand;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ManagerToCenterCommandServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.type=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("type");
		writer.sb.append(':');
		writer.sb.append(this.type);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		switch(type)
		{
			case ManagerCommandType.Agent:
			{
				AgentControl.agentClass();
			}
				break;
			case ManagerCommandType.ReloadConfig:
			{
				CenterC.main.reloadConfig(null);
			}
				break;
		}
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.type=0;
	}
	
}
