package com.home.commonGame.net.request.activity;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 活动重置(generated by shine) */
public class ActivityResetRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.ActivityReset;
	
	public int id;
	
	/** 是否到时间自然触发 */
	public boolean atTime;
	
	/** 下个重置时间 */
	public long nextTime;
	
	public ActivityResetRequest()
	{
		_dataID=GameRequestType.ActivityReset;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ActivityResetRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("id");
		writer.sb.append(':');
		writer.sb.append(this.id);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("nextTime");
		writer.sb.append(':');
		writer.sb.append(this.nextTime);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("atTime");
		writer.sb.append(':');
		writer.sb.append(this.atTime);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.id);
		
		stream.writeLong(this.nextTime);
		
		stream.writeBoolean(this.atTime);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeLong(this.nextTime);
		
		stream.writeBoolean(this.atTime);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.id=0;
		this.nextTime=0L;
		this.atTime=false;
	}
	
	/** 创建实例 */
	public static ActivityResetRequest create(int id,long nextTime,boolean atTime)
	{
		ActivityResetRequest re=(ActivityResetRequest)BytesControl.createRequest(dataID);
		re.id=id;
		re.nextTime=nextTime;
		re.atTime=atTime;
		return re;
	}
	
}
