package com.home.commonGame.net.request.func.auction;
import com.home.commonBase.data.item.auction.AuctionItemData;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.request.func.base.FuncSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 拍卖行添加自己的出售物品消息(generated by shine) */
public class FuncAuctionAddSaleItemRequest extends FuncSRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.FuncAuctionAddSaleItem;
	
	/** 出售物品数据 */
	public AuctionItemData data;
	
	public FuncAuctionAddSaleItemRequest()
	{
		_dataID=GameRequestType.FuncAuctionAddSaleItem;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		AuctionItemData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(AuctionItemData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncAuctionAddSaleItemRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.data!=null)
		{
			stream.writeDataFullNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("AuctionItemData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static FuncAuctionAddSaleItemRequest create(int funcID,AuctionItemData data)
	{
		FuncAuctionAddSaleItemRequest re=(FuncAuctionAddSaleItemRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.data=data;
		return re;
	}
	
}
