package com.home.commonGame.net.request.func.auction;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.request.func.base.FuncSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 回复获取拍卖行物品推荐价格(generated by shine) */
public class FuncReGetAuctionItemSuggestPriceRequest extends FuncSRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.FuncReGetAuctionItemSuggestPrice;
	
	/** 物品id */
	public int itemID;
	
	/** 单价 */
	public int price;
	
	public FuncReGetAuctionItemSuggestPriceRequest()
	{
		_dataID=GameRequestType.FuncReGetAuctionItemSuggestPrice;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncReGetAuctionItemSuggestPriceRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.itemID);
		
		stream.writeInt(this.price);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.itemID);
		
		stream.writeInt(this.price);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("itemID");
		writer.sb.append(':');
		writer.sb.append(this.itemID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("price");
		writer.sb.append(':');
		writer.sb.append(this.price);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.itemID=0;
		this.price=0;
	}
	
	/** 创建实例 */
	public static FuncReGetAuctionItemSuggestPriceRequest create(int funcID,int itemID,int price)
	{
		FuncReGetAuctionItemSuggestPriceRequest re=(FuncReGetAuctionItemSuggestPriceRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.itemID=itemID;
		re.price=price;
		return re;
	}
	
}
