package com.home.commonGame.net.request.func.item;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.request.func.base.FuncSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 添加单个物品数目消息(generated by shine) */
public class FuncAddOneItemNumRequest extends FuncSRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.FuncAddOneItemNum;
	
	/** 途径 */
	public int way;
	
	/** 序号 */
	public int index;
	
	/** 数目 */
	public int num;
	
	public FuncAddOneItemNumRequest()
	{
		_dataID=GameRequestType.FuncAddOneItemNum;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncAddOneItemNumRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("way");
		writer.sb.append(':');
		writer.sb.append(this.way);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("index");
		writer.sb.append(':');
		writer.sb.append(this.index);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("num");
		writer.sb.append(':');
		writer.sb.append(this.num);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.way);
		
		stream.writeInt(this.index);
		
		stream.writeInt(this.num);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.way);
		
		stream.writeInt(this.index);
		
		stream.writeInt(this.num);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.way=0;
		this.index=0;
		this.num=0;
	}
	
	/** 创建实例 */
	public static FuncAddOneItemNumRequest create(int funcID,int way,int index,int num)
	{
		FuncAddOneItemNumRequest re=(FuncAddOneItemNumRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.way=way;
		re.index=index;
		re.num=num;
		return re;
	}
	
}
