package com.home.commonGame.net.request.func.item;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.request.func.base.FuncSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 改变格子数(generated by shine) */
public class FuncRefreshItemGridNumRequest extends FuncSRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.FuncRefreshItemGridNum;
	
	/** 格子数 */
	public int gridNum;
	
	public FuncRefreshItemGridNumRequest()
	{
		_dataID=GameRequestType.FuncRefreshItemGridNum;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncRefreshItemGridNumRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.gridNum);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.gridNum);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("gridNum");
		writer.sb.append(':');
		writer.sb.append(this.gridNum);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.gridNum=0;
	}
	
	/** 创建实例 */
	public static FuncRefreshItemGridNumRequest create(int funcID,int gridNum)
	{
		FuncRefreshItemGridNumRequest re=(FuncRefreshItemGridNumRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.gridNum=gridNum;
		return re;
	}
	
}
