package com.home.commonGame.net.request.func.item;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.request.func.base.FuncSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 使用物品结果消息(generated by shine) */
public class FuncUseItemResultRequest extends FuncSRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.FuncUseItemResult;
	
	/** 道具ID(使用的) */
	public int itemID;
	
	/** 数目 */
	public int num;
	
	/** 结果 */
	public boolean result;
	
	public FuncUseItemResultRequest()
	{
		_dataID=GameRequestType.FuncUseItemResult;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncUseItemResultRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("itemID");
		writer.sb.append(':');
		writer.sb.append(this.itemID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("num");
		writer.sb.append(':');
		writer.sb.append(this.num);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("result");
		writer.sb.append(':');
		writer.sb.append(this.result);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.itemID);
		
		stream.writeInt(this.num);
		
		stream.writeBoolean(this.result);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.itemID);
		
		stream.writeInt(this.num);
		
		stream.writeBoolean(this.result);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.itemID=0;
		this.num=0;
		this.result=false;
	}
	
	/** 创建实例 */
	public static FuncUseItemResultRequest create(int funcID,int itemID,int num,boolean result)
	{
		FuncUseItemResultRequest re=(FuncUseItemResultRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.itemID=itemID;
		re.num=num;
		re.result=result;
		return re;
	}
	
}
